using WASP.Packing;
using WASP.Validation; using WASP.Packing; using WASP.Config; using WASP.Packing; using System; using WASP.Packing; using WASP.Validation; using WASP.UI;  namespace WASP.Core {     static class CommandRouter     {         public static void Route(string[] args)         {             if (args.Length == 0)             {                 ShowHelp();                 return;             }              string cmd = args[0].ToLower();              switch (cmd)             {                 case "unpack":                     if (args.Length < 2)                     {                         Console.WriteLine("Usage: unpack <file>");                         return;                     }                     Unpacker.Run(args[1]);                     break;                  case "pack":                     if (args.Length < 2)                     {                         Console.WriteLine("Usage: pack <folder>");                         return;                     }                     Packer.Run(args[1]);                     break;                  case "validate":                     if (args.Length < 2)                     {                         Console.WriteLine("Usage: validate <file>");                         return;                     }                     Validator.Run(args[1]);                     break;                  case "setup":                     SetupWizard.Run();                     break;                  default:                     ShowHelp();                     break;             }         }          static void ShowHelp()         {             Console.WriteLine("=== WASP COMMANDS ===");             Console.WriteLine("unpack <file>     - Unpack a .beemod_pack file");             Console.WriteLine("pack <folder>     - Pack a folder into a .beemod_pack file");             Console.WriteLine("validate <file>   - Validate the info.json and file structure");             Console.WriteLine("setup             - Run the setup wizard");         }     } }
